css:
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 11px;
    color: #222;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 2px solid #000;
    padding-bottom: 10px;
    margin-bottom: 20px;
}

.logo img {
    max-height: 80px;
}

.title {
    text-align: right;
}

.title h1 {
    margin: 0;
    font-size: 22px;
    text-transform: uppercase;
}

.meta {
    width: 100%;
    margin-bottom: 20px;
}

.meta td {
    padding: 4px 6px;
    vertical-align: top;
}

.badges {
    margin-top: 6px;
}

.badge {
    display: inline-block;
    border: 1px solid #000;
    padding: 2px 6px;
    margin-right: 6px;
    font-size: 9px;
    text-transform: uppercase;
    font-weight: bold;
}

table.items {
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;
}

table.items th {
    border-bottom: 2px solid #000;
    text-align: left;
    padding: 6px;
    font-size: 10px;
    text-transform: uppercase;
}

table.items td {
    border-bottom: 1px solid #ddd;
    padding: 6px;
}

table.items td.numeric {
    text-align: right;
}

.totals {
    width: 40%;
    float: right;
    margin-top: 20px;
}

.totals td {
    padding: 6px;
}

.totals .label {
    text-align: left;
}

.totals .value {
    text-align: right;
}

.totals .grand {
    border-top: 2px solid #000;
    font-weight: bold;
    font-size: 12px;
}

.section-title {
    margin-top: 30px;
    margin-bottom: 10px;
    font-weight: bold;
    text-transform: uppercase;
    font-size: 11px;
    border-bottom: 1px solid #000;
    padding-bottom: 4px;
}

.clearfix {
    clear: both;
}
