css:
/* ============================================================
   GLOBAL RESET (wkhtmltopdf safe)
   ============================================================ */

body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 11px;
    color: #222;
}

table {
    border-collapse: collapse;
    width: 100%;
}

td, th {
    vertical-align: top;
}

/* ============================================================
   HEADER
   ============================================================ */

.header {
    width: 100%;
    border-bottom: 2px solid #000;
    padding-bottom: 10px;
    margin-bottom: 20px;
}

.logo img {
    max-height: 80px;
}

.title {
    text-align: right;
}

.title h1 {
    margin: 0;
    font-size: 22px;
    text-transform: uppercase;
}

/* ============================================================
   META
   ============================================================ */

.meta {
    width: 100%;
    margin-bottom: 20px;
}

.meta td {
    padding: 4px 6px;
}

/* ============================================================
   BADGES
   ============================================================ */

.badges {
    margin-top: 6px;
}

.badge {
    display: inline-block;
    border: 1px solid #000;
    padding: 2px 6px;
    margin-right: 6px;
    font-size: 9px;
    text-transform: uppercase;
    font-weight: bold;
}

/* ============================================================
   ITEMS TABLE
   ============================================================ */

table.items {
    width: 100%;
    margin-top: 20px;
}

table.items th {
    border-bottom: 2px solid #000;
    text-align: left;
    padding: 6px;
    font-size: 10px;
    text-transform: uppercase;
}

table.items td {
    border-bottom: 1px solid #ddd;
    padding: 6px;
}

table.items td.numeric {
    text-align: right;
}

/* ============================================================
   TOTALS (CRITICAL: PDF SAFE)
   ============================================================ */

table.totals {
    width: 45%;
    margin-left: auto;
    margin-top: 20px;
    border-collapse: collapse;
}

table.totals tr {
    border: none;
}

table.totals td {
    padding: 6px;
    border: none;
    background: transparent;

    /* HARD RESET – kills float/flex bugs in wkhtmltopdf */
    float: none !important;
    display: table-cell !important;
    position: static !important;
}

table.totals td.label {
    text-align: left;
    white-space: nowrap;
}

table.totals td.value {
    text-align: right;
}

table.totals tr.grand td {
    border-top: 2px solid #000;
    font-weight: bold;
    font-size: 12px;
}

/* ============================================================
   SECTIONS
   ============================================================ */

.section-title {
    margin-top: 30px;
    margin-bottom: 10px;
    font-weight: bold;
    text-transform: uppercase;
    font-size: 11px;
    border-bottom: 1px solid #000;
    padding-bottom: 4px;
}

/* ============================================================
   SAFETY OVERRIDES (DO NOT REMOVE)
   ============================================================ */

.clearfix {
    display: none !important;
}

* {
    box-sizing: border-box;
}
